document.addEventListener("DOMContentLoaded",function(){const n=document.getElementById("contactForm");n&&n.addEventListener("submit",function(e){e.preventDefault();const o=this.querySelector('[name="name"]').value;setTimeout(()=>{c(`شكراً ${o}، تم إرسال رسالتك بنجاح وسنرد عليك في أقرب وقت`,"success"),this.reset()},1e3)});function c(e,o){const t=document.getElementById("notification");t&&(t.textContent=e,t.className=`notification ${o} show`,setTimeout(()=>{t.classList.remove("show")},5e3))}document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(o){o.preventDefault();const t=this.getAttribute("href");if(t==="#")return;const i=document.querySelector(t);i&&window.scrollTo({top:i.offsetTop-80,behavior:"smooth"})})})});
